function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
	//alert("control is " + control + "and newValue is " + newValue + " and isTemplate is " + isTemplate);

	if (newValue == 13) {
		alert("State is now Requested");
	}
	else if (newValue == 14) {
		alert("State is now Awaiting Approval");
	}
	else if (newValue == 15) {
		alert("State is now Approved");
	}
	else if (newValue == 16) {
		alert("State is now Awaiting Feedback");
	}
	else if (newValue == 3) {
		alert("State is now Close Complete");
	}
	else if (newValue == 8) {
		alert("State is now Withdrawn");
	}



   //Type appropriate comment here, and begin script below

}
